const staticPDFFile = ``

func TestReaderCanReadPDF(t *testing.T) {
	files, err := os.ReadDir("../../fixtures/testfiles")
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	for _, f := range files {
		ext := filepath.Ext(f.Name())
		if ext != ".pdf" {
			continue
		}

		fileName := f.Name()
		t.Run(fileName, func(st *testing.T) {
			st.Parallel()

			input_file, err := os.Open("../../fixtures/testfiles/" + fileName)
			if err != nil {
				st.Errorf("%s: %s", fileName, err.Error())
				return
			}

			finfo, err := input_file.Stat()
			if err != nil {
				input_file.Close()
				st.Errorf("%s: %s", fileName, err.Error())
				return
			}
			size := finfo.Size()

			_, err = pdf.NewReader(input_file, size)
			if err != nil {
				input_file.Close()
				st.Errorf("%s: %s", fileName, err.Error())
				return
			}

			input_file.Close()
		})

	}
}

func TestSignPDF(t *testing.T) {
	_ = os.RemoveAll("../../fixtures/testfiles/failed/")
	_ = os.MkdirAll("../../fixtures/testfiles/failed/", 0o777)

	files, err := os.ReadDir("../../fixtures/testfiles")
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	certificate_data_block, _ := pem.Decode([]byte(signCertPem))
	if certificate_data_block == nil {
		t.Errorf("failed to parse PEM block containing the certificate")
		return
	}

	cert, err := x509.ParseCertificate(certificate_data_block.Bytes)
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	key_data_block, _ := pem.Decode([]byte(signKeyPem))
	if key_data_block == nil {
		t.Errorf("failed to parse PEM block containing the private key")
		return
	}

	pkey, err := x509.ParsePKCS1PrivateKey(key_data_block.Bytes)
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	certificate_chains := make([][]*x509.Certificate, 0)

	for _, f := range files {
		f := f

		ext := filepath.Ext(f.Name())
		if ext != ".pdf" {
			continue
		}

		t.Run(f.Name(), func(st *testing.T) {
			st.Log("Signing file", f.Name())

			input_file, err := os.Open("../../fixtures/testfiles/" + f.Name())
			if err != nil {
				st.Errorf("%s: %s", f.Name(), err.Error())
				return
			}

			finfo, err := input_file.Stat()
			if err != nil {
				input_file.Close()
				st.Errorf("%s: %s", f.Name(), err.Error())
				return
			}
			size := finfo.Size()

			rdr, err := pdf.NewReader(input_file, size)
			if err != nil {
				input_file.Close()
				st.Errorf("%s: %s", f.Name(), err.Error())
				return
			}

			outputFile, err := os.CreateTemp("", "pdfsign_test_"+f.Name())
			if err != nil {
				t.Errorf("%s", err.Error())
				return
			}

			err = Sign(input_file, outputFile, rdr, size, SignData{
				Signature: SignDataSignature{
					Info: SignDataSignatureInfo{
						Name:        "John Doe",
						Location:    "Somewhere",
						Reason:      "Test",
						ContactInfo: "None",
						Date:        time.Now().Local(),
					},
					CertType:   CertificationSignature,
					DocMDPPerm: AllowFillingExistingFormFieldsAndSignaturesPerms,
				},
				Signer:            pkey,
				Certificate:       cert,
				CertificateChains: certificate_chains,
				TSA: TSA{
					URL: "http://timestamp.digicert.com",
				},
				RevocationData:     revocation.InfoArchival{},
				RevocationFunction: DefaultEmbedRevocationStatusFunction,
			})

			if err != nil {
				input_file.Close()
				_ = os.Remove(outputFile.Name())
				st.Errorf("%s: %s", f.Name(), err.Error())
				return
			}

			_, err = verify.File(outputFile)
			input_file.Close()
			if err != nil {
				err2 := os.Rename(outputFile.Name(), "../../fixtures/testfiles/failed/"+filepath.Base(input_file.Name()))
				if err2 != nil {
					st.Error(err2)
				}
				st.Errorf("%s: %s", f.Name(), err.Error())
			} else {
				os.Remove(outputFile.Name())
			}
		})
	}
}

func TestSignPDFFile(t *testing.T) {
	certificate_data_block, _ := pem.Decode([]byte(signCertPem))
	if certificate_data_block == nil {
		t.Errorf("failed to parse PEM block containing the certificate")
		return
	}

	cert, err := x509.ParseCertificate(certificate_data_block.Bytes)
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	key_data_block, _ := pem.Decode([]byte(signKeyPem))
	if key_data_block == nil {
		t.Errorf("failed to parse PEM block containing the private key")
		return
	}

	pkey, err := x509.ParsePKCS1PrivateKey(key_data_block.Bytes)
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	tmpfile, err := os.CreateTemp("", "pdfsign_test")
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	err = SignFile("../../fixtures/testfiles/testfile20.pdf", tmpfile.Name(), SignData{
		Signature: SignDataSignature{
			Info: SignDataSignatureInfo{
				Name:        "John Doe",
				Location:    "Somewhere",
				Reason:      "Test",
				ContactInfo: "None",
				Date:        time.Now().Local(),
			},
			CertType:   CertificationSignature,
			DocMDPPerm: AllowFillingExistingFormFieldsAndSignaturesPerms,
		},
		DigestAlgorithm: crypto.SHA512,
		Signer:          pkey,
		Certificate:     cert,
	})

	if err != nil {
		os.Remove(tmpfile.Name())
		t.Errorf("%s: %s", "testfile20.pdf", err.Error())
		return
	}

	_, err = verify.File(tmpfile)
	if err != nil {
		t.Errorf("%s: %s", tmpfile.Name(), err.Error())

		err2 := os.Rename(tmpfile.Name(), "../../fixtures/testfiles/failed/testfile20.pdf")
		if err2 != nil {
			t.Error(err2)
		}
	} else {
		os.Remove(tmpfile.Name())
	}
}

func TestSignPDFFileUTF8(t *testing.T) {
	certificate_data_block, _ := pem.Decode([]byte(signCertPem))
	if certificate_data_block == nil {
		t.Errorf("failed to parse PEM block containing the certificate")
		return
	}

	cert, err := x509.ParseCertificate(certificate_data_block.Bytes)
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	key_data_block, _ := pem.Decode([]byte(signKeyPem))
	if key_data_block == nil {
		t.Errorf("failed to parse PEM block containing the private key")
		return
	}

	pkey, err := x509.ParsePKCS1PrivateKey(key_data_block.Bytes)
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	tmpfile, err := os.CreateTemp("", "pdfsign_test")
	if err != nil {
		t.Errorf("%s", err.Error())
		return
	}

	signerName := "姓名"
	signerLocation := "位置"
	err = SignFile("../../fixtures/testfiles/testfile20.pdf", tmpfile.Name(), SignData{
		Signature: SignDataSignature{
			Info: SignDataSignatureInfo{
				Name:        signerName,
				Location:    signerLocation,
				Reason:      "Test with UTF-8",
				ContactInfo: "None",
				Date:        time.Now().Local(),
			},
			CertType:   CertificationSignature,
			DocMDPPerm: AllowFillingExistingFormFieldsAndSignaturesPerms,
		},
		DigestAlgorithm: crypto.SHA512,
		Signer:          pkey,
		Certificate:     cert,
	})

	if err != nil {
		os.Remove(tmpfile.Name())
		t.Errorf("%s: %s", "testfile20.pdf", err.Error())
		return
	}

	info, err := verify.File(tmpfile)
	if err != nil {
		t.Errorf("%s: %s", tmpfile.Name(), err.Error())

		err2 := os.Rename(tmpfile.Name(), "../../fixtures/testfiles/failed/testfile20.pdf")
		if err2 != nil {
			t.Error(err2)
		}
	} else {
		if info.Signers[0].Name != signerName {
			t.Errorf("expected %q, got %q", signerName, info.Signers[0].Name)
		}
		if info.Signers[0].Location != signerLocation {
			t.Errorf("expected %q, got %q", signerLocation, info.Signers[0].Location)
		}

		os.Remove(tmpfile.Name())
	}
}

func BenchmarkSignPDF(b *testing.B) {
	certificate_data_block, _ := pem.Decode([]byte(signCertPem))
	if certificate_data_block == nil {
		b.Errorf("failed to parse PEM block containing the certificate")
		return
	}

	cert, err := x509.ParseCertificate(certificate_data_block.Bytes)
	if err != nil {
		b.Errorf("%s", err.Error())
		return
	}

	key_data_block, _ := pem.Decode([]byte(signKeyPem))
	if key_data_block == nil {
		b.Errorf("failed to parse PEM block containing the private key")
		return
	}

	pkey, err := x509.ParsePKCS1PrivateKey(key_data_block.Bytes)
	if err != nil {
		b.Errorf("%s", err.Error())
		return
	}

	certificate_chains := make([][]*x509.Certificate, 0)

	data, err := base64.StdEncoding.DecodeString(staticPDFFile)
	if err != nil {
		b.Errorf("%s: %s", "testfile20.pdf", err.Error())
		return
	}

	input_file := filebuffer.New(data)
	size := int64(len(data))

	rdr, err := pdf.NewReader(input_file, size)
	if err != nil {
		input_file.Close()
		b.Errorf("%s: %s", "testfile20.pdf", err.Error())
		return
	}

	for n := 0; n < b.N; n++ {
		if _, err := input_file.Seek(0, 0); err != nil {
			b.Errorf("%s: %s", "testfile20.pdf", err.Error())
			return
		}

		err = Sign(input_file, io.Discard, rdr, size, SignData{
			Signature: SignDataSignature{
				Info: SignDataSignatureInfo{
					Name:        "John Doe",
					Location:    "Somewhere",
					Reason:      "Test",
					ContactInfo: "None",
					Date:        time.Now().Local(),
				},
				CertType:   CertificationSignature,
				DocMDPPerm: AllowFillingExistingFormFieldsAndSignaturesPerms,
			},
			Signer:            pkey,
			Certificate:       cert,
			CertificateChains: certificate_chains,
			RevocationData:    revocation.InfoArchival{},
		})

		if err != nil {
			b.Errorf("%s: %s", "testfile20.pdf", err.Error())
			return
		}
	}

	input_file.Close()
}
